import {Table} from "react-bootstrap";
import React from "react";

function UserTable(props) {

    const filterBySearch = (info) => {
        const fullName = `${user.name.first} ${user.name.last}`; 
        return !props.search || fullName.toLowerCase().includes(props.search.toLowerCase()) 
    }

    const sortByLocation = (userA, userB) => {
        const locationA = `${userA.location.city}, ${userA.location.state}`;
        const locationB = `${userB.location.city}, ${userB.location.state}`;

        if (locationA < locationB) {
            return -1;
        }

        if (locationB < locationA) {
            return 1;
        }

        return 0;
    }
    return (
        <Table striped bordered hover>
            <thead>
                <tr>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Phone # </th>
                    <th>Location </th>
                        <button onClick={ () => updateSearchType("asc")}>Ascend</button>
                        <button onClick={() => updateSearchType("Des")}>Descend</button>
                    <th>Email </th>
                </tr>
            </thead>
            <tbody>
                {props.users.filter(filterBySearch).map(info => {
                    return (
                        <tr key={info.id.value}>
                        <td>{info.name.first}</td>
                        <td>{info.name.last}</td>
                        <td>{info.phone}</td>
                        <td>{info.email}</td>
                        <td>{info.location.city}, {info.location.state}</td>
                        </tr>
                        )
                })}
            </tbody>
        </Table>
    )
}

export default UserTable;
